**conditional variable**: to synchronize the execution of threads</pre>
``` cpp
#include<iostream>
#include<condition_variable>
#include<mutex>
#include<deque>
#include<thread>
using namespace std;

deque<int> q;
mutex mu;
condition_variable cv;

void producer(){
  int count = 10;
  while(count > 0){
    unique_lock<mutex> lock(mu);
    q.push_front(count);
    cout << "produce data: " << count << endl;
    lock.unlock();
    cv.notify_one();
    count--;
    this_thread::sleep_for(chrono::seconds(1));
  }
}
void consumer(){
  int data = 0;
  while(data != 1){
    unique_lock<mutex> lock(mu);
    cv.wait(lock, [](){return !q.empty();}); // prevent spurious wake
    data = q.back();
    q.pop_back();
    cout << "consume data: " << data << endl;
    lock.unlock();
  }
}
int main(){
  thread t1(producer);
  thread t2(consumer);
  t1.join();
  t2.join();
}
```

