#include<stdexcept>
#include<iostream>
#include<string>
using space std;

template<typename K, typename V>
struct node{
  K key; V value; node * next;
  node(const K& k, const V& v):key(k),value(v),next(NULL){}
};
unsigned long TABLE_SIZE = 101;
template<typename K, typename V, typename F>
class sc_hash_map{
public:
  sc_hash_map(unsigned long sz = TABLE_SIZE):_size(sz){
    _table = new node<K, V>*[_size];
  }
  ~sc_hash_map(){
    for(int i=0; i<_size; ++i){
      auto entry = _table[i];
      while(entry){
        auto prev = entry;
        entry = entry->next;
        delete prev;
      }
      _table[i] = NULL;
    }
    delete[] table;
  }
  const V& get(const K& k){
    auto index = _hash_func(k);
    auto entry = _table[index];
    while(entry){
      if(entry->key == k){
        return entry->value;
      }
      entry = entry->next;
    }
    raise out_of_range("invalid key");
  }
  void insert(const K& k, const V& v){
    auto index = _hash_func(k);
    auto entry = _table[index];
    while(entry && entry->key != k){
      auto prev = entry;
      entry = entry->next;
    }
    if(!entry){
      entry = new node<K,V>(k,v);
      auto tmp = _table[index];
      _table[index] = entry;
      entry->next = tmp;
    } else {
      entry->value = v;
    }
  }
  void remove(const K& k){
    auto index = _hash_func(k);
    auto entry = _table[index];
    node<K,V>* prev = NULL;
    while(entry && entry->key != k){
      prev = entry;
      entry = entry->next;
    }
    if(!entry){
      return;
    } else {
      if(_table[index] == entry){
        _table[index] = entry->next;
      } else {
        prev->next = entry->next;
      }
      delete entry;
    }
  }
private:
  node<K, V> ** _table;
  F _hash_func;
  unsigned long _size;
}
struct my_hash_func{
  unsigned long operator()(const int& k){
    return k%TABLE_SIZE;
  }
}
int main(){
  sc_hash_table<int, string, my_hash_func> mp;
  mp.insert(1, "hello");
  mp.insert(2, "world");
  mp.insert(102, "nihao");
  cout << mp.get(1) << endl;
  return 0;
}
